import React from 'react'; 
import { Component } from'react'
import ProductsDisplay from './ProductsDisplay.jsx'
const endpoint = 'https://dog.ceo/api/breeds/image/random/20 '


class ContentContainer extends Component {

  state = {
    imageUrls: []
  }

  componentDidMount() {
    return fetch(endpoint)
      .then(response => response.json())
      .then(jsonData => {
         const imageData = jsonData.message
         this.setState({ productUrls: imageData })
      })
      .catch(error => console.log(error)) 
  }; 

  render() {
    return (
      <ImagesContext.Provider
        value={{
          imageUrls: this.state.imageUrls,
          handleClick: this.handleClick,
        }}>
        <div className="content-container">
          <ProductsDisplay count={this.state.imageUrls.length} />
        </div>
      </ImagesContext.Provider>
    );
  }
}

export default ContentContainer;
