const endpoint = 'https://swapi.co/api/people'

class MainContainer extends Component {
  constructor(props){
    super(props)
    this.state = {
      gifList: [] 
    };
  };

   componentDidMount() {
    return fetch(endpoint)
     .then(response => response.json())
     .then(gifList => {
      console.log(gifList)
       let gifUrls = []
       gifList = gifList.results 
       for (let i = 0; i < gifList.length; i++) {
         //gifUrls are an array of image tagged
         gifUrls.push("<img " + gifList[i].url + ">")
       }
       this.setState({ gifList: gifUrls })
     })
     .catch(error => console.log(error)) 
  }; 







